import React from 'react'
import Navbar from '../layouts/Navbar'
import Footer from '../layouts/Footer'
import {useEffect, useState} from 'react'


function AllCryptoDetails() {
    const [cryptoData, setCryptoData] = useState()
    useEffect(()=>{
        let search = window.location.search
        let params = new URLSearchParams(search)
        let foo = params.get('q')
        fetch(`https://api.coingecko.com/api/v3/coins/${foo}?localization=true&tickers=false&market_data=true&community_data=true&developer_data=false&sparkline=true`)
        .then((res)=> res.json())
        .then((data)=>setCryptoData(data))
    },[])

    if(!cryptoData){
        return(
            <div className='container'>
                <Navbar />
                <Footer />
            </div>
        )
    }
    return (
        <div className='container'>
            <Navbar />
            <div className='showCryptoHeader'>
                <img src={`${cryptoData.image.large}`} className='showCryptoImg'/>
                <h2>{cryptoData.name} - {cryptoData.symbol}</h2>
                <h6>Since {cryptoData.genesis_date}</h6>
            </div>
            <div className='showCryptoContent'>
                <p>Highest price(24h): {cryptoData.market_data.high_24h.usd} USD <br />Lowest price(24h): {cryptoData.market_data.low_24h.usd} USD <br /> Price change(24h): {Math.floor(cryptoData.market_data.price_change_24h_in_currency.usd * 1000)/1000} USD</p>
                <b><p>Current price in different currencies</p></b> 
                <p>üá∫üá∏ {cryptoData.market_data.current_price.usd}usd üáµüá± {cryptoData.market_data.current_price.pln}pln üá™üá∫ {cryptoData.market_data.current_price.eur}eur üáØüáµ {cryptoData.market_data.current_price.jpy}jpy<br/> üá®üá≥{cryptoData.market_data.current_price.cny}cny üá¨üáß{cryptoData.market_data.current_price.gbp}gbp üá¶üá∫{cryptoData.market_data.current_price.aud}audüá®üá≠{cryptoData.market_data.current_price.chf}chf</p>
                <b><p></p></b> 
                <p>üëç{Math.floor(cryptoData.sentiment_votes_up_percentage* 10)/10} üëé {Math.ceil(cryptoData.sentiment_votes_down_percentage* 10)/10}</p>
                <a href={cryptoData.links.homepage}>Visit official {cryptoData.name} website</a>
            </div>
            <Footer />
        </div>
    );
}

export default AllCryptoDetails
