import React from 'react'
import Navbar from '../layouts/Navbar'
import Footer from '../layouts/Footer'
import {useEffect, useState} from 'react'


function AllCryptoDetails() {
    const [cryptoData, setCryptoData] = useState()
    useEffect(()=>{
        let search = window.location.search
        let params = new URLSearchParams(search)
        let foo = params.get('q')
        fetch(`https://api.coingecko.com/api/v3/coins/${foo}?localization=true&tickers=false&market_data=true&community_data=true&developer_data=false&sparkline=true`)
        .then((res)=> res.json())
        .then((data)=>setCryptoData(data))
    },[])

    if(!cryptoData){
        return(
            <div className='container'>
                <Navbar />
                <Footer />
            </div>
        )
    }
    return (
        <div className='container'>
            <Navbar />
            <div className='showCryptoHeader'>
                <img src={`${cryptoData.image.large}`} className='showCryptoImg'/>
                <h2>{cryptoData.name} - {cryptoData.symbol}</h2>
            </div>
                <p>Highest price(24h): {cryptoData.market_data.high_24h.usd} USD <br />Lowest price(24h): {cryptoData.market_data.low_24h.usd} USD <br /> Price change(24h): {Math.floor(cryptoData.market_data.price_change_24h_in_currency.usd * 1000)/1000} USD</p>
                <b><p>Current price in different currencies</p></b> 
                <p>ðŸ‡µðŸ‡± {cryptoData.market_data.current_price.pln}pln ðŸ‡ºðŸ‡¸ {cryptoData.market_data.current_price.usd}usd ðŸ‡ªðŸ‡º {cryptoData.market_data.current_price.eur}eur ðŸ‡¨ðŸ‡³ {cryptoData.market_data.current_price.chf}chf</p>
                {/* <p className='caption'>{cryptoData.name} was created in {cryptoData.genesis_date}. Hashing algorithm of {cryptoData.name}: {cryptoData.hashing_algorithm}</p>
                <p>Socials: {cryptoData.links.twitter_screen_name} on twitter. {cryptoData.links.facebook_username} on Facebook. </p>
                <p>Votes up: {cryptoData.sentiment_votes_up_percentage} Votes down: {cryptoData.sentiment_votes_down_percentage}</p>
                <p>Current price: {cryptoData.market_data.current_price.pln} pln {cryptoData.market_data.current_price.usd} usd {cryptoData.market_data.current_price.eur} eur {cryptoData.market_data.current_price.chf} chf</p> */}
                <a href={cryptoData.links.homepage}>Visit official {cryptoData.name} website</a>
            <Footer />
        </div>
    );
}

export default AllCryptoDetails
