import './App.css';
import React, {useState, useEffect} from 'react';
import Crypto from './Components/Crypto'

function App() {
  const [cryptos, setCryptos] = useState(null)
    useEffect(()=>{
      fetch('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false')
      .then((res)=> res.json())
      .then((data)=>setCryptos(data))
    },[])
    if(!cryptos){
      console.log('loading data')
      return(
        <div>Data loading...</div>
      )
    }
  return (
    <div className="App">
      {cryptos  && cryptos.map(data=>{
        return <Crypto 
          name={data.name} current_price={data.current_price} image={data.image} 
          highest_24h={data.high_24h}lowest_24h={data.low_24h} price_change_percentage_24h={data.price_change_percentage_24h} symbol={data.symbol} ath={data.ath}
        />
      })}
    </div>
  );
}

export default App;
